import React, { useState } from "react";
import styled from "styled-components";

const Section = styled.section`
  min-height: 100vh;
  width: 100vw;
  margin: 0 auto;
  overflow: hidden;
  display: flex;
  justify-content: flex-start;
  align-items: flex-start;
  position: relative;
`;

const Title = styled.h1`
  font-size: ${(props) => props.theme.fontxxxl};
  font-family: "Kaushan Script";
  font-weight: 300;
  text-shadow: 1px 1px 1px ${(props) => props.theme.body};
  color: ${(props) => props.theme.text};
  position: absolute;
  top: 1rem;
  left: 5%;
  z-index: 11;
`;

const Left = styled.div`
  width: 35%;
  background-color: ${(props) => props.theme.body};
  color: ${(props) => props.theme.text};
  min-height: 100vh;
  z-index: 5;
  position: fixed;
  left: 0;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  padding: 2rem;
`;

const Right = styled.div`
  position: absolute;
  left: 35%;
  padding-left: 10%;
  min-height: 100vh;
  background-color: ${(props) => props.theme.grey};
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`;

const UploadContainer = styled.div`
  background: white;
  padding: 2rem;
  border-radius: 10px;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.1);
  text-align: center;
  width: 80%;
`;

const Input = styled.input`
  display: none;
`;

const Label = styled.label`
  background-color: ${(props) => props.theme.text};
  color: ${(props) => props.theme.body};
  padding: 0.8rem 2rem;
  border-radius: 5px;
  cursor: pointer;
  display: inline-block;
  margin-top: 1rem;
`;

const Button = styled.button`
  background-color: ${(props) => props.theme.text};
  color: ${(props) => props.theme.body};
  padding: 0.8rem 2rem;
  border-radius: 5px;
  cursor: pointer;
  margin-top: 1rem;
  border: none;
  font-size: 1rem;
  transition: all 0.3s ease;

  &:hover {
    background-color: ${(props) => props.theme.textLight};
  }
`;

const Select = styled.select`
  padding: 0.5rem;
  margin-top: 1rem;
  border-radius: 5px;
  width: 100%;
`;

const Shop = () => {
  const [file, setFile] = useState(null);
  const [includeImages, setIncludeImages] = useState(false);
  const [theme, setTheme] = useState("blue");
  const [uploading, setUploading] = useState(false);
  const [downloadLink, setDownloadLink] = useState("");

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!file) {
      alert("請選擇一個 Word 文件");
      return;
    }

    setUploading(true);
    setDownloadLink("");

    const formData = new FormData();
    formData.append("file", file);
    formData.append("parse_images", includeImages);
    formData.append("theme", JSON.stringify({ background: theme, text: "#000000" })); // 送出 JSON 給後端

    try {
      const response = await fetch("http://localhost:8000/upload", {
        method: "POST",
        body: formData,
      });

      if (!response.ok) {
        throw new Error("上傳失敗！");
      }

      const data = await response.json();
      setDownloadLink(data.pptx_url);
    } catch (error) {
      console.error(error);
      alert("上傳失敗，請稍後再試！");
    } finally {
      setUploading(false);
    }
  };

  return (
    <Section id="mainContent">
      <Title>Upload Your Document</Title>
      <Left>
        <p>
          Welcome to SlideCraft!  
          <br />
          <br />
            Turn your Word documents into professional presentations effortlessly 
          <br />
          <br />
          <strong>How to use?</strong>
          <br />
            Step1: Click the button on the right to upload a `.docx` file  
          <br />
            Step2: Choose whether to <strong>include images</strong>  
          <br />
            Step3: Select a <strong>presentation theme  </strong>
          <br />
            Step4: Click <strong>"Generate Slides"</strong>, and let AI handle the rest! 
        </p>
      </Left>
      <Right>
        <UploadContainer>
          <h2>Upload Your Word File</h2>
          <Input type="file" id="fileUpload" accept=".docx" onChange={handleFileChange} />
          <Label htmlFor="fileUpload">Choose File</Label>
          {file && <p> {file.name}</p>}

          <div>
            <label>
              <input
                type="checkbox"
                checked={includeImages}
                onChange={(e) => setIncludeImages(e.target.checked)}
              />{" "}
              Include Images?
            </label>
          </div>

          <Select onChange={(e) => setTheme(e.target.value)} value={theme}>
            <option value="blue">Blue Theme</option>
            <option value="green">Green Theme</option>
            <option value="red">Red Theme</option>
          </Select>

          <Button onClick={handleUpload} disabled={uploading}>
            {uploading ? "Uploading..." : "Generate Slides"}
          </Button>

          {downloadLink && (
            <Button as="a" href={downloadLink} download>
              Download Presentation
            </Button>
          )}
        </UploadContainer>
      </Right>
    </Section>
  );
};

export default Shop;
